import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/fontawesome-free'
import { faGoogle } from '@fortawesome/free-solid-svg-icons'

export default function Login() {
  return (
    <Container className="container-sm">
      <AuthCard>
				<Title>Masuk</Title>
				<div className="input-group">
					<LogoGoogle className="input-group-text" id="basic-addon1"><FontAwesomeIcon icon={['fab', 'fa-google']} /></LogoGoogle>
					<ButtonGoogle type="submit" className="btn btn-primary">Masuk dengan Google</ButtonGoogle>
				</div>
				<Divider className="hr-label">
					<span>atau</span>
				</Divider>
				<form>
					<div className="input-group mb-3">
						<input type="text" className="form-control" placeholder="Email" maxLength={30} aria-label="Email" aria-describedby="basic-addon1" required/>
					</div>
					<div className="input-group mb-3">
						<input type="password" className="form-control" placeholder="Kata sandi" maxLength={30} aria-label="Password" aria-describedby="basic-addon1" required/>
					</div>
					<div className="btn-group" role="group" aria-label="Basic example">
						<button type="submit" className="btn btn-primary">Masuk</button>
					</div>
				</form>
			</AuthCard>
    </Container>
  )
}

const Container = styled.div`
	height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
`;

const AuthCard = styled.div`
	width: 400px;
	height: 500px;
	padding: 20px 50px;
	border-radius: 8px;
	background-color: #FFF;
	box-shadow: 10px 10px 45px #EAEAEA;
`;

const Title = styled.p`
	font-size: 24px;
	font-weight: 500;
	font-family: 'Work Sans',
`;

const LogoGoogle = styled.div`
	width: 15%;
	justify-content: center;
`;

const ButtonGoogle = styled.button`
	width: 85%;
	justify-content: center;
`;

const Divider = styled.div`
	clear: both;
	display: flex;
	margin-top: 20px;
	overflow: hidden;
	position: relative;
	text-align: center;
	align-items: center;
	margin-bottom: 20px;
	justify-content: center;
`
